import React from "react"
import { useState,useEffect } from "react";
function App() {
  const [value,setValue] = useState({})
  const submit_handler = (e) => {
    console.log(e.target.value);
    setValue(...value, {[e.taget.name]:e.target.value})
  }
  return (
    <>
    <label>Title:-</label>
    <input placeholder="title" name="input1" onChange={submit_handler}/>
    <label>Author:-</label>
    <input placeholder="author" name="input2" onChange={submit_handler}/>
    <button type="button" onClick={submit_handler}>Submit</button>
    </>
    );
}

export default App;
