import React from "react"
import { useState } from "react";
import data from "./Data";
function App() {
  const [value,setValue] = useState({})
  const input_handler = (e) => {
    setValue({...value, [e.target.name]:e.target.value})
    // console.log("Input handler ==============")
  }
  const submit_handler = async (e) => {
    data.push(value);
    alert("Data added")
    // console.log("Submit handler ==============")
  }
  return (
    <>
    {/* {console.log("App componenet before submit handler ==============")} */}
    <label>Title:-</label>
    <input placeholder="title" name="title" onChange={input_handler}/>
    <label>Author:-</label>
    <input placeholder="author" name="author" onChange={input_handler}/>
    <button type="button" onClick={submit_handler}>Submit</button>
    <DataEffect/>
    {/* {console.log("App componenet after submit handler ==============")} */}
    </>
    );
}

export default App;
